public class Solution {
    public long MaximumValueSum(int[][] board) {
        var foo = new List<(int, int)>[board.Length];

            for (int i = 0; i < board.Length; i++)
            {
                foo[i] = new List<(int, int)>(); // value, index in a row

                for (int j = 0; j < board[0].Length; j++)
                {
                    foo[i].Add((board[i][j], j));
                }
                foo[i] = foo[i].OrderBy(x => x.Item1).ToList();
            }

            long result = long.MinValue;

            for (int i = 0; i < board.Length; i++)
            {
                var first = foo[i].Last();
                for (int j = 0; j < board.Length; j++)
                {
                    if (j == i) continue;
                    var second = foo[j][foo[j].Count() - 1].Item2 == first.Item2 ? foo[j][foo[i].Count() - 2] : foo[j][foo[j].Count() - 1];
                    for (int k = 0; k < board.Length; k++)
                    {
                        if (k == i || k == j) continue;
                        int size = foo[k].Count();
                        var third = foo[k][size - 1].Item2 == first.Item2 || foo[k][size - 1].Item2 == second.Item2
                            ? foo[k][size - 2].Item2 == first.Item2 || foo[k][size - 2].Item2 == second.Item2
                            ? foo[k][size - 3]
                            : foo[k][size - 2]
                            : foo[k][size - 1];

                        result = Math.Max(result, (long)first.Item1 + (long)second.Item1 + (long)third.Item1);
                    }
                }
            }

            return result;
    }
}
