class Solution {
public:

    typedef long long ll;

    long long maximumValueSum(vector<vector<int>>& v) {
        ll res=0, m=v.size(), n=v[0].size();
        vector<pair<pair<ll, ll>, ll>> pos;
        for(int i=0; i<m; i++){
            vector<pair<ll, ll>> cur;
            for(int j=0; j<n; j++){
                cur.push_back({v[i][j], j});
            }
            sort(cur.begin(), cur.end());
            reverse(cur.begin(), cur.end());
            for(int j=0; j<3; j++){
                pos.push_back({{i, cur[j].second}, cur[j].first});
            }       
        }
        for(int j=0; j<n; j++){
            vector<pair<ll, ll>> cur;
            for(int i=0; i<m; i++){
                cur.push_back({v[i][j], i});
            }
            sort(cur.begin(), cur.end());
            reverse(cur.begin(), cur.end());
            for(int i=0; i<3; i++){
                pos.push_back({{cur[i].second, j}, cur[i].first});
            }
        }
        res=-1e18;
        for(int i=0; i<pos.size(); i++){
            for(int j=i+1; j<pos.size(); j++){
                for(int k=j+1; k<pos.size(); k++){
                    ll x1=pos[i].first.first, y1=pos[i].first.second, val1=pos[i].second;
                    ll x2=pos[j].first.first, y2=pos[j].first.second, val2=pos[j].second;
                    ll x3=pos[k].first.first, y3=pos[k].first.second, val3=pos[k].second;
                    if(x1!=x2 && x1!=x3 && x2!=x3 && y1!=y2 && y1!=y3 && y2!=y3)res=max(res, val1+val2+val3);
                }
            }
        }
        return res;
    }
};
