class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<long long,long long>>>v(n,vector<pair<long long,long long>>(m));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v[i][j] = {board[i][j],j};
            }
            sort(v[i].begin(),v[i].end());
            reverse(v[i].begin(),v[i].end());
        }
        long long ans=-1e16;

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){


                    for(int a=0;a<3;a++){
                        for(int b=0;b<3;b++){
                            for(int c=0;c<3;c++){
                                // if(a==b || b==c || c==a)continue;

                                long long x=v[i][a].second , y = v[j][b].second, z = v[k][c].second;
                                if(x!=y && y!=z && z!=x){
                                    long long val=v[i][a].first+v[j][b].first+v[k][c].first;
                                    ans=max(ans,val);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
