class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        multiset<pair<long long,pair<int,int>>,greater<pair<int,pair<int,int>>>> s;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                s.insert({board[i][j],{i,j}});
            }
        }
        int cnt=0;
        vector<pair<long long,pair<int,int>>> v;
        while(cnt<2*(n+m)+1 && cnt<n*m){
            auto it=*(s.begin());
            v.push_back(it);
            s.erase(it);
            cnt++;
        }
        long long mx=-5e9;
        for(int i=0;i<v.size();i++){
            int x1=v[i].second.first;
            int y1=v[i].second.second;
            int x2=-1;
            int y2=-1;
            int x3=-1;
            int y3=-1;
            for(int j=0;j<v.size();j++){
                x2=v[j].second.first;
                y2=v[j].second.second;
                if(x2==x1 || y2==y1) continue;
                for(int k=0;k<v.size();k++){
                    x3=v[k].second.first;
                     y3=v[k].second.second;
                     if(x3==x2 || x3==x1 || y3==y2 || y3==y1){
                        continue;
                     }
                     mx=max(mx,v[i].first+v[j].first+v[k].first);
                }
            }
        }
        return mx;


    }
};
