class Solution {
    int[][][] max;
    long find(int x,int y,int z){
         long ans = Long.MIN_VALUE;
         boolean flag = true;
         for(int i=0;i<3;i++){
             for(int j=0;j<3;j++){
                 if(max[x][i][1] != max[y][j][1]){
                     for(int k=0;k<3;k++){
                        if(max[x][i][1] != max[z][k][1] && max[z][k][1] != max[y][j][1]){
                            ans = Math.max(ans,(long)max[x][i][0]+max[y][j][0]+max[z][k][0]);
                        }
                     }
                 }

             }
         }
         //System.out.println(x+" "+y+" "+z+" "+ans);
         return ans;
    }
    public long maximumValueSum(int[][] board) {
        int n = board.length,m = board[0].length;
        max = new int[n][3][3];
        for(int i=0;i<n;i++){
            int[][] a = new int[m][2];
            for(int j=0;j<m;j++){
                a[j][0] = board[i][j];
                a[j][1] = j;
            }
            Arrays.sort(a,(x,y)->{
               if(x[0] != y[0]){
                   return Integer.compare(y[0],x[0]);
               } 
               return Integer.compare(x[1],y[1]);
            });
            for(int j=0;j<3;j++){
                max[i][j][0] = a[j][0];
                max[i][j][1] = a[j][1];
            }  
        }
        long ans = Long.MIN_VALUE;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    ans = Math.max(ans,find(i,j,k));
                }
            }
        }
        //System.out.println("=====");
        return ans;
    }
}
