class Solution {
public:

    long long getAns(int ind1, int ind2, int ind3, int n, int m, vector<vector<pair<int, int>>> &v){
        long long ans = -1e18;
        for(int i = 0; i < 3; i++){
            for(int j = 0; j < 3; j++){
                if(v[ind1][i].second == v[ind2][j].second) continue;
                for(int k = 0; k < 3; k++){
                    if(v[ind2][j].second != v[ind3][k].second && v[ind3][k].second != v[ind1][i].second) {
                        ans = max(ans, -(v[ind1][i].first * 1LL + v[ind2][j].first + v[ind3][k].first));
                    }
                }
            }
        }

        return ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size(), m = board[0].size();
        vector<vector<pair<int, int>>> v(n, vector<pair<int,int>> (m));
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                v[i][j].first = -board[i][j];
                v[i][j].second = j;
            }
            sort(v[i].begin(), v[i].end());
        }

        long long ans = -1e18;

        for(int i = 0; i < n; i++){
            for(int j = i + 1; j < n; j++){
                for(int k = j + 1; k < n; k++){

                    ans = max(ans, getAns(i, j, k, n, m, v));
                }
            }
        }

        return ans;
    }
};
