class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int r=b.size(),c=b[0].size();
        vector<vector<pair<int,int>>>t(r);
        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                t[i].push_back({b[i][j],j});
            }
            sort(t[i].begin(),t[i].end());
        }
        long long ans=-1e10;
        for(int i=0;i<r;i++){
            for(int j=i+1;j<r;j++){
                for(int k=j+1;k<r;k++){
                    for(int a=1;a<=3;a++){
                        for(int b=1;b<=3;b++){
                            for(int cc=1;cc<=3;cc++){
                                auto p1=t[i][c-a];
                                auto p2=t[j][c-b];
                                auto p3=t[k][c-cc];
                                if(p1.second==p2.second || p2.second==p3.second || p3.second==p1.second )continue;
                                long long temp=0ll+p1.first+p2.first+p3.first;
                                ans=max(ans,temp);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
