long long solve(set<pair<int,int>>&a,set<pair<int,int>>&b,set<pair<int,int>>&c){
    long long ans=a.rbegin()->first;
    int ind=a.rbegin()->second;
    int indB=0;

    if (ind==b.rbegin()->second){
        auto pt=b.rbegin();
        pt++;
        ans+=pt->first;
        indB=pt->second;

    }else{
        ans+=b.rbegin()->first;
        indB=b.rbegin()->second;
    }
    auto xx=c.rbegin();
    while(xx->second==ind ||xx->second==indB){
        xx++;
    }
    ans+=xx->first;
    return ans;
}

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<set<pair<int,int>>>r(board.size());
        for(int i=0;i<board.size();i++){
            for(int j=0;j<board[i].size();j++){
                r[i].insert({board[i][j],j});
                if(r[i].size()>3){
                    r[i].erase(r[i].begin());
                }
            }
        }

        long long ans=-4000000000;
        int n=board.size();

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                 ans=max(ans,solve(r[i],r[j],r[k]));
                 ans=max(ans,solve(r[i],r[k],r[j]));
                 ans=max(ans,solve(r[j],r[i],r[k]));
                 ans=max(ans,solve(r[j],r[k],r[i]));
                 ans=max(ans,solve(r[k],r[j],r[i]));
                 ans=max(ans,solve(r[k],r[i],r[j]));
                }
            }
        }
        return ans;
    }
};
