class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> all(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                all[i].push_back({board[i][j], j});
            }
        }
        for(int i=0;i<n;i++){
            sort(all[i].begin(), all[i].end());
        }
        long long res = LLONG_MIN;
        function<long long(int,int,int)> cal = [&](int i, int j, int k){
            //cout<<i<<" "<<j<<" "<<k<<endl;
            long long res = all[i][m-1].first;
            int t1 = all[i][m-1].second;
            if(all[j][m-1].second != t1){
                int t2 = all[j][m-1].second;
                res += all[j][m-1].first;
                if(all[k][m-1].second != t1 && all[k][m-1].second != t2){
                    res += all[k][m-1].first;
                }
                else if(all[k][m-2].second != t1 && all[k][m-2].second != t2){
                    res += all[k][m-2].first;
                }
                else{
                    res += all[k][m-3].first;
                }
            }
            else{
                int t2 = all[j][m-2].second;
                res += all[j][m-2].first;
                if(all[k][m-1].second != t1 && all[k][m-1].second != t2){
                    res += all[k][m-1].first;
                }
                else if(all[k][m-2].second != t1 && all[k][m-2].second != t2){
                    res += all[k][m-2].first;
                }
                else{
                    res += all[k][m-3].first;
                }
            }
            //cout<<res<<endl;
            return res;
        };
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    res = max({res, cal(i,j,k), cal(i,k,j), cal(j,i,k), cal(j,k,i), cal(k,i,j), cal(k,j,i)});
                }
            }
        }
        return res;
    }
};
