class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long n=board.size();
        long long m=board[0].size();
        long long ans=-1000000000000000000;
        vector< vector<pair<long long,long long>>>v(n+1);
        for(long long i=n-1;i>=2;i--){
            if(i!=n-1){
                v[i]=v[i+1];
            }
            for(long long j=0;j<m;j++){
                v[i].push_back({board[i][j],j});
            }
            sort(v[i].rbegin(),v[i].rend());
        }
        for(long long i1=0;i1<n-2;i1++){
            for(long long j1=0;j1<m;j1++){
                long long x=board[i1][j1];
                for(long long i2=i1+1;i2<n-1;i2++){
                    for(long long j2=0;j2<m;j2++){
                        if(j2==j1){
                            continue;
                        }
                        long long y=board[i2][j2];
                        long long z=0;
                        for(long long x=0;x<v[i2+1].size();x++){
                            if(v[i2+1][x].second!=j1&&v[i2+1][x].second!=j2){
                                z=v[i2+1][x].first;
                                break;
                            }
                        }
                        long long total=x+y+z;
                        if(ans<total){
                            ans=total;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
