class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        vector<vector<vector<long long>>>v;
        for(int i=0;i<n;i++){
            vector<vector<long long>>temp;
            for(int j=0;j<m;j++){
                temp.push_back({board[i][j],j});
            }
            sort(temp.rbegin(),temp.rend());
            v.push_back({temp[0],temp[1],temp[2]});
        }
        unordered_map<int,int>c;
        long long x,ans=(long long)board[0][0]+(long long)board[1][1]+(long long)board[2][2];
        for(int i=0;i<n;i++){
            for(int j=0;j<3;j++){
                x=v[i][j][0];
                c[v[i][j][1]]=1;

                for(int i2=i+1;i2<n;i2++){
                    for(int j2=0;j2<3;j2++){
                        if(c[v[i2][j2][1]]){
                            continue;
                        }
                        x+=v[i2][j2][0];
                        c[v[i2][j2][1]]=1;

                        for(int i3=i2+1;i3<n;i3++){
                            for(int j3=0;j3<3;j3++){
                                if(c[v[i3][j3][1]]){
                                    continue;
                                }
                                ans=max(ans,x+v[i3][j3][0]);
                            }
                        }
                        x-=v[i2][j2][0];
                        c[v[i2][j2][1]]=0;
                    }
                }
                c[v[i][j][1]]=0;
            }
        }
        return ans;

    }
};
