class Solution {
    public long maximumValueSum(int[][] board) {
        int rn = board.length;
        int cn = board[0].length;


        List<List<int[]>> rowMaxVal = new ArrayList<>(); 

        for (int i = 0; i < rn; ++i) {
            List<int[]> maxInRow = new ArrayList<>(Arrays.asList(new int[]{Integer.MIN_VALUE, -1}, new int[]{Integer.MIN_VALUE, -1}, new int[]{Integer.MIN_VALUE, -1}));
            for (int j = 0; j < cn; ++j) {
                if (board[i][j] > maxInRow.get(0)[0]) {
                    maxInRow.set(2, maxInRow.get(1));
                    maxInRow.set(1, maxInRow.get(0));
                    maxInRow.set(0, new int[]{board[i][j], j});
                } else if (board[i][j] > maxInRow.get(1)[0]) {
                    maxInRow.set(2, maxInRow.get(1));
                    maxInRow.set(1, new int[]{board[i][j], j});
                } else if (board[i][j] > maxInRow.get(2)[0]) {
                    maxInRow.set(2, new int[]{board[i][j], j});
                }
            }
            rowMaxVal.add(maxInRow);
        }

        long maxS = Long.MIN_VALUE;

        for (int r1 = 0; r1 < rn - 2; ++r1) {
            for (int r2 = r1 + 1; r2 < rn - 1; ++r2) {
            for (int r3 = r2 + 1; r3 < rn; ++r3) {

                for (int[] val1 : rowMaxVal.get(r1)) {
                            for (int[] val2 : rowMaxVal.get(r2)) {
                                if (val1[1] == val2[1]) continue;

                                for (int[] val3 : rowMaxVal.get(r3)) {
                                    if (val1[1] != val3[1] && val2[1] != val3[1]) {
                                        long currS = (long) val1[0] + val2[0] + val3[0];
                                        maxS = Math.max(maxS, currS);
                                    }
                                }
                            }
                }
            }
            }
        }

        return maxS;
    }
}
