class Solution {
public:
	long long maximumValueSum(vector<vector<int>> grid) {
		int n = grid.size(), m = grid[0].size();
		vector<vector<pair<int,int>>>x(n);
		for (int i = 0; i < n; i++) {
			for (int j = 0; j < m; j++) {
				x[i].push_back({ grid[i][j],j });
			}
			sort(x[i].rbegin(), x[i].rend());
			while (x[i].size() > 3)x[i].pop_back();
		}
		long long ret = -4e9;
		for (int i = 0; i < n; i++) {
			for (int j = i + 1; j < n; j++) {
				for (int k = j + 1; k < n; k++) {
					int sz = min(3, (int)(x[i].size()));
					for (int a = 0; a < sz; a++) {
						for (int b = 0; b < sz; b++) {
							if (x[j][b].second == x[i][a].second)continue;
							for (int c = 0; c < sz; c++) {
								if (x[j][b].second == x[k][c].second)continue;
								if (x[i][a].second == x[k][c].second)continue;
								ret = max(ret, 1LL * x[i][a].first + 1LL * x[j][b].first + 1LL * x[k][c].first);
							}
						}
					}
				}
			}
		}
		return ret;
	}
};
