class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long ans = LLONG_MIN;

        vector<vector<pair<int, int>>> row_max(m, vector<pair<int, int>>(3));
        for (int i = 0; i < m; i++) {
            priority_queue<pair<int, int>> pq;
            for (int j = 0; j < n; j++) {
                pq.push({board[i][j], j});
            }
            row_max[i][0] = pq.top();
            pq.pop();
            row_max[i][1] = pq.top();
            pq.pop();
            row_max[i][2] = pq.top();
            pq.pop();
        }

        for (int i1 = 0; i1 <= m - 3; i1++) {
            for (int i2 = i1 + 1; i2 <= m - 2; i2++) {
                for (int i3 = i2 + 1; i3 <= m - 1; i3 ++) {
                    for (int j1 = 0; j1 < 3; j1++) {
                        for (int j2 = 0; j2 < 3; j2++) {
                            if (row_max[i2][j2].second == row_max[i1][j1].second) continue;
                            for (int j3 = 0; j3 < 3; j3++) {
                                if (row_max[i3][j3].second == row_max[i1][j1].second) continue;
                                if (row_max[i3][j3].second == row_max[i2][j2].second) continue;
                                ans = max(ans, (long long)row_max[i1][j1].first + row_max[i2][j2].first + row_max[i3][j3].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
