class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        n = len(board)
        m = len(board[0])
        f1 = [-float("inf")]*m
        f2 = [-float("inf")]*m
        ans = -float("inf")
        for x in range(n):
            cands1 = sorted(((f1[i], i) for i in range(m)), reverse = True)
            cands2 = sorted(((board[x][i], i) for i in range(m)), reverse = True)
            for y in range(m):
                ans = max(ans, f2[y] + board[x][y])
                f2cand = -float("inf")
                for i1 in range(min(6, m)):
                    if cands1[i1][1] == y:
                        continue
                    for i2 in range(min(6, m)):
                        if cands2[i2][1] == y or cands1[i1][1] == cands2[i2][1]:
                            continue
                        f2cand = max(f2cand, cands1[i1][0] + cands2[i2][0])
                f2[y] = max(f2[y], f2cand)
                f1[y] = max(f1[y], board[x][y])
        return ans
