class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=LLONG_MIN;
        int m=board.size(), n=board[0].size();
        vector<vector<pair<int, int>>> pr(m, vector<pair<int, int>>(n));
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                pr[i][j]={board[i][j], j};
            }
            sort(pr[i].begin(), pr[i].end());
            reverse(pr[i].begin(), pr[i].end());
        }
        long long temp=0;
        int r1=0, r2=0, r3=0, c1=0, c2=0, c3=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<3;j++){
                temp+=pr[i][j].first;
                r1=i;
                c1=pr[i][j].second;
                for(int i2=0;i2<m;i2++){
                    for(int j2=0;j2<3;j2++){
                        if(i2!=r1 && pr[i2][j2].second!=c1){
                            temp+=pr[i2][j2].first;
                            r2=i2;
                            c2=pr[i2][j2].second;
                            for(int i3=0;i3<m;i3++){
                                for(int j3=0;j3<3;j3++){
                                    int nc=pr[i3][j3].second;
                                    if((i3!=r1 && i3!=r2) && (nc!=c1 && nc!=c2)){
                                        ans=max(ans, temp+pr[i3][j3].first);
                                    }
                                }
                            }
                            temp-=pr[i2][j2].first;
                        }
                    }
                }
                temp=0;
            }
        }
        return ans;
    }
};
