class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        pre = [-inf] * n
        maxv = [[] for _ in range(m)]
        for i in range(m - 1, -1, -1):
            for j in range(n):
                # pre[j] = max(pre[j], board[i][j])
                maxv[i].append((board[i][j], j))
            maxv[i].sort(reverse=True)

        row = [0] * n
        ans = -inf
        for i in range(m - 2):
            cur = 0
            for vi, ji in maxv[i][:3]:
                row[ji] = 1
                cur = vi
                for j in range(i + 1, m - 1):
                    for vj, jj in maxv[j][:3]:
                        if row[jj]: continue
                        row[jj] = 1
                        cur += vj
                        for k in range(j + 1, m):
                            for vk, jk in maxv[k][:3]:
                                if row[jk]: continue
                                ans = max(ans, cur + vk)
                                # print(cur + vk, i, ji, j, jj, k, jk)
                        row[jj] = 0
                        cur -= vj
                row[ji] = 0

        return ans
