class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<pair<int,pair<int,int>>> a;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                a.push_back({board[i][j], {i, j}});
            }
        }

        sort(a.begin(), a.end());
        reverse(a.begin(), a.end());

        int l = min(20, (int)a.size());

        long long int ans = -1e18;

        for (int i = 0; i < l; i++) {
            for (int j = i + 1; j < l; j++) {
                for (int k = j + 1; k < l; k++) {
                    int x1 = a[i].second.first;
                    int y1 = a[i].second.second;

                    int x2 = a[j].second.first;
                    int y2 = a[j].second.second;

                    int x3 = a[k].second.first;
                    int y3 = a[k].second.second;

                    long long temp = 1LL * a[i].first + 1LL * a[j].first + 1LL * a[k].first;

                    if (x1 == x2 || x1 == x3 || x2 == x3 || y1 == y2 || y1 == y3 || y2 == y3) {
                        continue;
                    }

                    ans = max(ans, temp);
                }
            }
        }

        return ans;
    }
};
