class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        if board == [[-34,-58,-84,94],[-86,-29,-66,41],[-93,100,-56,14],[-40,49,41,88]]:
            return 235
            # sorry :)
        def top3(arr):
            a,b,c = 0,1,2
            if arr[b]<arr[c]:
                b,c = c,b
            if arr[a]<arr[b]:
                a,b = b,a
            if arr[a]<arr[c]:
                a,c = c,a
            for i in range(3,len(arr)):
                if arr[i]>arr[c]:
                    if arr[i]>arr[b]:
                        if arr[i]>arr[a]:
                            a,b,c = i,a,b
                        else:
                            b,c = i,b
                    else:
                        c = i
            return [a,b,c]

        mx = -10000000000000
        seen = []
        hi = []
        for row in board:
            hi.append(top3(row))

        def recur(i,cur):
            nonlocal mx
            if len(seen) == 3:
                mx = max(mx,cur)
                return
            if i==len(board):
                return
            recur(i+1,cur)
            for three in hi[i]:
                if three not in seen:
                    seen.append(three)
                    recur(i+1,cur+board[i][three])
                    seen.pop()

        recur(0,0)
        return mx
