    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<int>>answers(board.size());
        for(int i=0;i<board.size();i++){
            vector<pair<int,int>>p;
            for(int j=0;j<board[i].size();j++){
                p.push_back({board[i][j],j});
            }
            sort(p.begin(),p.end());
            reverse(p.begin(),p.end());
        vector<int>helper;
            for(int j=0;j<3;j++){
                helper.push_back(p[j].second);
            }
            answers[i]=helper;
        }
        long long answ=-10000000000;
        for(int i=0;i<board.size();i++){
            for(int j=i+1;j<board.size();j++){
                for(int k=j+1;k<board.size();k++){
                    long long val=ans(i,j,k,board,answers);
                    long long val1=ans(i,k,j,board,answers);
                    long long val2=ans(j,i,k,board,answers);
                    long long val3=ans(j,k,i,board,answers);
                    long long val4=ans(k,j,i,board,answers);
                    long long val5=ans(k,i,j,board,answers);
                    answ=max({answ,val,val1,val2,val3,val4,val5});
                }
            }
        }
        return answ;
    }
