#define ll long long

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        ll n=board.size();
        ll m=board[0].size();

        vector<vector<pair<ll,ll>>> grid(n,vector<pair<ll,ll>>(m));
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                grid[i][j]={board[i][j],j};
            }
            sort(grid[i].rbegin(),grid[i].rend());
        }

        ll ans=-1e18;

        for(int i=0; i<n; i++) {
            for(int j=i+1; j<n; j++) {
                for(int k=j+1; k<n; k++) {
                    for(int i1=0; i1<3; i1++) {
                        for(int j1=0; j1<3; j1++) {
                            for(int k1=0; k1<3; k1++) {
                                if(grid[i][i1].second!=grid[j][j1].second && grid[j][j1].second!=grid[k][k1].second && grid[k][k1].second!=grid[i][i1].second) {
                                    ans=max(ans,grid[i][i1].first+grid[j][j1].first+grid[k][k1].first);
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
