class Solution {
public:
    long long dp[101][101][101];
    long long solve(int i,int prev1,int prev2,vector<vector<int>>& board){
        if(i>=board.size())
            return -1e18;
        if(dp[i][prev1+1][prev2+1]!=-1)
            return dp[i][prev1+1][prev2+1];
        long long ans1 = solve(i+1,prev1,prev2,board);
        long long ans2 = -1e18;
        if(prev1!=-1 && prev2!=-1){
            long long maxi=-1e18;
            for(int j=0;j<board[0].size();j++){
                if(prev1==j || prev2==j)
                    continue;
                maxi=max(maxi,(long long)board[i][j]);
            }
            return dp[i][prev1+1][prev2+1]=max(maxi,ans1);
        }
        for(int j=0;j<board[0].size();j++){
            if(prev1==j || prev2==j){
                continue;
            }
            int new_prev1=0;
            int new_prev2=0;
            if(prev1==-1)
            {
                new_prev1=j;
                new_prev2=prev2;
            }

            else
            {
                new_prev2=j;
                new_prev1=prev1;
            }

            ans2 = max(ans2,board[i][j]+solve(i+1,new_prev1,new_prev2,board));
        }
        return dp[i][prev1+1][prev2+1]=max(ans1,ans2);
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=0;
        int n=board.size();
        int m=board[0].size();
        memset(dp,-1,sizeof(dp));
        return solve(0,-1,-1,board);
    }
};
