class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<int,pair<int,int>>> a;
        int n = board.size();
        int m = board[0].size();
        for(int i = 0;i<n;i++){
            for(int j = 0;j<m;j++){
                a.push_back({board[i][j],{i,j}});
            }
        }
        sort(a.begin(),a.end(),greater<pair<int,pair<int,int>>>());
        long long max1 = LLONG_MIN;
        int x = 0;
        for(int i = 0;i<a.size();i++){
            for(int j = i+1;j<a.size();j++){
                for(int k = j+1;k<a.size();k++){
                    x++;
                    if(x>1e7){
                        break;
                    }
                    if(a[i].second.first!=a[j].second.first && a[j].second.first!=a[k].second.first && a[i].second.first!=a[k].second.first){
                        if(a[i].second.second!=a[j].second.second && a[j].second.second!=a[k].second.second && a[i].second.second!=a[k].second.second){
                            long long f = a[i].first;
                            f+=a[j].first;
                            f+=a[k].first;
                            max1 = max(max1,f);
                        }
                    }
                }
            }
        }
        return max1;
    }
};
