class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        rows, cols = len(board), len(board[0])
        three_max_lines = [[] for i in range(rows)]
        for i in range(rows):
            cur = []
            for j in range(cols):
                cur.append((board[i][j], j))
            cur.sort(reverse=True)
            count = 0
            for j in range(cols):
                if count >= 3 and cur[j][0] != cur[j - 1][0]:
                    break
                else:
                    three_max_lines[i].append(cur[j])
                    count += 1
        max_sum = float(-inf)
        for r1 in range(rows):
            for r2 in range(r1 + 1, rows):
                for val1, col1 in three_max_lines[r1]:
                    for val2, col2 in three_max_lines[r2]:
                        if col1 != col2:
                            for r3 in range(rows):
                                if r3 != r1 and r3 != r2:
                                    for val3, col3 in three_max_lines[r3]:
                                        if col3 != col1 and col3 != col2:
                                            current_sum = val1 + val2 + val3
                                            max_sum = max(max_sum, current_sum)
                                            break
                            break
        return max_sum
