#pragma GCC optimize("Ofast")
#define ll long long
#define pii pair<ll, ll>
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<pii>> v;
        for (int i = 0; i < n; i++) {
            int m1=-1e9, m2=-1e9, m3=-1e9;
            int m1i, m2i, m3i;
            for (int j = 0; j < m; j++) {
                if (board[i][j] > m1) {
                    m3 = m2;
                    m3i = m2i;
                    m2 = m1;
                    m2i = m1i;
                    m1 = board[i][j];
                    m1i = j;
                }
                else if (board[i][j] > m2) {
                    m3 = m2;
                    m3i = m2i;
                    m2 = board[i][j];
                    m2i = j;
                }
                else if (board[i][j] > m3) {
                    m3 = board[i][j];
                    m3i = j;
                }
            }
            v.push_back({{m1, m1i}, {m2, m2i}, {m3, m3i}});
        }
        ll best = -1e18;
        for (int i = 0; i < n; i++) {
            for (int j = i+1; j < n; j++) {
                for (int k = j+1; k < n; k++) {
                    // select rows i, j, k and determine best
                    vector<pii> &x = v[i], &y = v[j], &z = v[k];
                    for (int l = 0; l < 3; l++) {
                        for (int o = 0; o < 3; o++) {
							if (x[l].second == y[o].second) continue;
                            for (int p = 0; p < 3; p++) {
								if (z[p].second == x[l].second || z[p].second == y[o].second) continue;
                                best = max(best, x[l].first + y[o].first + z[p].first);
                            }
                        }
                    }
                }
            }
        }
        return best;

    }
};
