class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])

        arr = []
        for i in range(m):
            for j in range(n):
                arr.append((board[i][j], i, j))
        arr.sort(reverse=True)
        ans = -inf
        for i in range(m + 1):
            for j in range(i + 1, m * 2 + 1):
                for h in range(j + 1, m * 3):
                    used_x = set()
                    used_y = set()
                    used_x.add(arr[i][1])
                    used_x.add(arr[j][1])
                    used_x.add(arr[h][1])
                    used_y.add(arr[i][2])
                    used_y.add(arr[j][2])
                    used_y.add(arr[h][2])
                    if len(used_x) == 3 and len(used_y) == 3:
                        ans = max(ans, arr[i][0] + arr[j][0] + arr[h][0])
        return ans
