#include <vector>
#include <algorithm>
#include <climits>

using namespace std;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long m = board.size();
        long long n = board[0].size();

        vector<vector<long long>> vals(m, vector<long long>(3, LLONG_MIN));
        vector<vector<long long>> cols(m, vector<long long>(3, -1));

        for (long long i = 0; i < m; i++) {
            vector<pair<long long, long long>> valcol;
            for (long long j = 0; j < n; j++) {
                valcol.push_back({(long long)board[i][j], j});
            }
            sort(valcol.rbegin(), valcol.rend()); 

            for (long long k = 0; k < 3 && k < valcol.size(); k++) {
                vals[i][k] = valcol[k].first;
                cols[i][k] = valcol[k].second;
            }
        }

        long long maxSum = LLONG_MIN;

        for (long long i = 0; i < m; i++) {
            for (long long j = i + 1; j < m; j++) {
                for (long long k = j + 1; k < m; k++) {
                    for (long long a = 0; a < 3; a++) {
                        for (long long b = 0; b < 3; b++) {
                            for (long long c = 0; c < 3; c++) {
                                long long col1 = cols[i][a];
                                long long col2 = cols[j][b];
                                long long col3 = cols[k][c];

                                if (col1 != col2 && col2 != col3 && col1 != col3) {
                                    long long curSum = vals[i][a] + vals[j][b] + vals[k][c];
                                    maxSum = max(maxSum, curSum);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
