     for(auto k : st) {
       vv.push_back(k); 
     }


      mm.push_back({vv[vv.size() - 1].first, vv[vv.size() - 1].second, i});
     mm.push_back({vv[vv.size() - 2].first, vv[vv.size() - 2].second, i});
