typedef long long LL;
typedef pair<LL, LL> PII;
#define x first
#define y second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        vector<pair<LL, PII>> w;

        for (int i = 0; i < n; i ++ )
            for (int j = 0; j < m; j ++ )
                w.push_back({a[i][j], {i, j}});

        sort(w.rbegin(), w.rend());
        while (w.size() > 100)  w.pop_back();

        LL res = -2e18;
        for (int i = 0; i < w.size(); i ++ ) {
            for (int j = i + 1; j < w.size(); j ++ ) {
                if (w[i].y.x == w[j].y.x || w[i].y.y == w[j].y.y)
                    continue;

                for (int k = j + 1; k < w.size(); k ++ ) {
                    if (w[i].y.x == w[k].y.x || w[i].y.y == w[k].y.y || w[j].y.x == w[k].y.x || w[j].y.y == w[k].y.y)
                        continue;

                    res = max(res, w[i].x + w[j].x + w[k].x);
                }
            }
        }
        return res;
    }
};
