typedef pair<int, int> pii;
typedef long long ll;
static constexpr int INF = 0x3f3f3f3f;
static constexpr int MAXN = 100005;
static constexpr int LIM = 22;


class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<int>> a(n + 1, vector<int>(m + 1, 0));
        for (int i = 0; i < n; i ++) {
            for (int j = 0; j < m; j ++) {
                a[i + 1][j + 1] = board[i][j];
            }
        }
        vector<vector<pii>> top(m + 1);
        for (int j = 1; j <= m; j ++) {
            vector<pii> tmp;
            for (int i = 1; i <= n; i ++) {
                tmp.emplace_back(a[i][j], i);
            }
            sort(tmp.begin(), tmp.end(), greater<>());
            top[j].push_back(tmp[0]); top[j].push_back(tmp[1]); top[j].push_back(tmp[2]);
        }
        ll ans = -1e18;
        for (int j1 = 1; j1 <= m - 2; j1 ++) {
            for (int j2 = j1 + 1; j2 <= m - 1; j2 ++) {
                for (int j3 = j2 + 1; j3 <= m; j3 ++) {

                    for (auto &a : top[j1]) {
                        for (auto &b : top[j2]) {
                            for (auto &c : top[j3]) {
                                if (a.second != b.second && b.second != c.second && a.second != c.second) {
                                    ans = max(ans, (ll)a.first + (ll)b.first + (ll)c.first);
                                }
                            }
                        }
                    }

                }

            }
        }
        return ans;

    }
};
