class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        int n = board.size();
        int m = board[0].size();

        vector<vector<pair<long long,long long> > > dp;
        long long ma1 = INT_MIN;
        long long ma2 = INT_MIN;
        long long ma3 = INT_MIN;

        long long index1 = INT_MIN;
        long long index2 = INT_MIN;
        long long index3 = INT_MIN;


        for(int j = 0;j<n;j++){


            ma1 = INT_MIN;
            ma2 = INT_MIN;
            ma3 = INT_MIN;

            for(int i = 0;i<m;i++){

                if(board[j][i]>=ma1){
                    ma3 = ma2;
                    ma2 = ma1;
                    ma1 = board[j][i];
                    index3 = index2;
                    index2 = index1;
                    index1 = i;
                }
                else if(board[j][i]>=ma2){
                    ma3 = ma2;
                    ma2 = board[j][i];
                    index3 = index2;
                    index2 = i;
                }
                else if(board[j][i]>ma3){
                    ma3 = board[j][i];
                    index3 = i;
                }

            }

            vector<pair<long long, long long> > temp;
            temp.push_back({ma1,index1});
            temp.push_back({ma2,index2});
            temp.push_back({ma3,index3});
            dp.push_back(temp);
        }
        long long ans = -1e12;
        for(int i = 0;i<n-2;i++){

            for(int j = i+1;j<n-1;j++){

                for(int k = j+1;k<n;k++){

                    for(int i1 = 0;i1<3;i1++){

                        for(int j1 = 0;j1<3;j1++){

                            for(int k1 = 0;k1<3;k1++){

                                if(dp[i][i1].second != dp[j][j1].second && dp[i][i1].second != dp[k][k1].second && dp[j][j1].second != dp[k][k1].second){
                                    ans = max(ans,dp[i][i1].first +dp[k][k1].first + dp[j][j1].first);
                                }

                            }

                        }

                    }

                }

            }

        }
        return ans;
    }
};
