class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        using ll = long long;
        int n = a.size();
        int m = a[0].size();
        long long ans = -1e18;
        vector s(n, vector<ll>(m + 1, -1e18));
        for (int i = 0; i < n; i++) {
            for (int j = m - 1; j >= 0; j--) {
                s[i][j] = max(s[i][j + 1], (ll)a[i][j]);
            }
        }
        vector<ll> mx(n, -1e18);
        for (int j = 0; j < m; j++) {
            vector<ll> b(n);
            for (int i = 0; i < n; i++) b[i] = s[i][j + 1];
            sort(b.rbegin(), b.rend());
            for (int i = 0; i < n; i++) {
                for (int k = 0; k < n; k++) {
                    if (i == k) continue;
                    int t = 0;
                    if (b[t] == max(s[i][j + 1], s[k][j + 1])) t++;
                    if (b[t] == min(s[i][j + 1], s[k][j + 1])) t++;
                    ans = max(ans, a[i][j] + mx[k] + b[t]);
                }
            }
            for (int i = 0; i < n; i++) mx[i] = max(mx[i], (ll)a[i][j]);
        }
        return ans;
    }
};
