class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        vals = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                vals.append((board[i][j], i, j))
        vals = sorted(vals, reverse=True)
        ys = set()
        xs = set()
        ans = -10**11
        for i in range(len(vals)):
            if ans >= vals[i][0] * 3:
                continue
            ys = set([vals[i][1]])
            xs = set([vals[i][2]])
            for j in range(i+1, len(vals)):
                if vals[j][1] in ys or vals[j][2] in xs:
                    continue
                if ans >= vals[i][0] + vals[j][0] * 2:
                    continue
                ys.add(vals[j][1])
                xs.add(vals[j][2]) 
                for k in range(j+1, len(vals)):
                    if vals[k][1] in ys or vals[k][2] in xs:
                        continue
                    ans = max(ans, vals[i][0] + vals[j][0] + vals[k][0])
                ys.remove(vals[j][1])
                xs.remove(vals[j][2]) 
        return ans
