class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<pair<long long,int> > >   vp(board.size());


        for ( int i=0; i<board.size(); i++ )
        {
            priority_queue<pair<long long,int>>   pq;

            for ( int k=0; k<board[i].size(); k++ )
                pq.push( make_pair(board[i][k], k) );

            for ( int j=0; j<3; j++ )
            {
                vp[i].push_back( pq.top() );
                pq.pop();
            }            

        }

        long long ans = -4 * 1e9;

        for ( int i=0; i<vp.size(); i++ )
        {
            // cout << "hi1 "<< endl; 
            for ( int i_e=0; i_e<3; i_e++ )
            {
                // cout << "hi2 "<< endl;
                for ( int j=0; j<vp.size(); j++ )
                {
                    // cout << "hi3 "<< endl;
                    for ( int j_e=0; j_e<3; j_e++ )
                    {
                        // cout << "hi4 "<< endl;
                        for ( int k=0; k<vp.size(); k++ )
                        {
                            for ( int k_e=0; k_e<3; k_e++ )
                            {
                                // cout << "hihi" << endl;
                                // cout << vp[i][i_e].first << " " << vp[j][j_e].first << " " << vp[k][k_e].first << endl;
                                if ( i == j or j == k or i == k )
                                    continue ;

                                if ( vp[i][i_e].second == vp[j][j_e].second or vp[j][j_e].second == vp[k][k_e].second or vp[i][i_e].second == vp[k][k_e].second )
                                    continue ;

                                // cout << vp[i][i_e].first << " " << vp[j][j_e].first << " " << vp[k][k_e].first << endl;
                                ans = max( ans, vp[i][i_e].first + vp[j][j_e].first + vp[k][k_e].first );
                            }
                        }
                    }
                }
            }
        }

        return ( ans );
    }
};
