class Solution {
public:
    long long res;
    int mx;
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<array<int,3>> all(n*m);
        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++) {
                all[i*m+j] = {board[i][j],i,j};
            }
        }
        sort(all.begin(),all.end());
        reverse(all.begin(),all.end());

        res = -1e9-10; res *= 3; 
        mx = min(m*n-1,400);
        for(int i=0;i<=mx;i++) 
            for(int j=i+1;j<=mx;j++) 
                for(int k=j+1;k<=mx;k++) {
                    if(all[i][1]==all[j][1]||all[i][1]==all[k][1]||all[j][1]==all[k][1]) continue;
                    if(all[i][2]==all[j][2]||all[i][2]==all[k][2]||all[j][2]==all[k][2]) continue;
                    res = max(res, 1ll*all[i][0]+all[j][0]+all[k][0]);
                }

            return res;
    }


};
