class Solution {
public:
#define ff                      first
#define ss                      second
int M;
    bool cmp(int &a, int &b) {
        int I = a / M, J = a % M;
        int I1 = b / M, J1 = b % M;
        if(I == I1 || J == J1) return false;
        return true;
    }
    long long maximumValueSum(vector<vector<int>>& ar) {
        vector<pair<long long,int>> tmp;
        int n = ar.size(), m = ar[0].size();
        M = m;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                tmp.push_back({ar[i][j], i * m + j});
            }
        }
        sort(tmp.rbegin(), tmp.rend());
        long long res = -1e15;
        int N = tmp.size();
        int cnt = 0;
        for(int i = 0; i < min(4, N); i++) {
            for(int j = i + 1; j < N; j++) if(cmp(tmp[i].ss, tmp[j].ss)) {
                for(int k = j + 1; k < N; k++) if(cmp(tmp[i].ss, tmp[k].ss) && cmp(tmp[j].ss, tmp[k].ss)) {
                    res = max(res, tmp[i].ff + tmp[j].ff + tmp[k].ff);
                    break;
                }
            }
        }

        return res;
    }
};
