class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        # 维护前三大
        m,n = len(board),len(board[0])
        li = []
        for i in range(m):
            li.append(sorted(zip(board[i],range(n)),reverse=True)[:3])
        one = [-inf] * n
        two = [-inf] * (n * n)
        ans = -inf
        for i in range(m):
            for x,idx in li[i]:
                for j in range(n * n):
                    if j // n != idx and j % n != idx:
                        ans = max(ans,two[j] + x)
            for x,idx in li[i]:
                for j in range(n):
                    if j != idx:
                        two[j * n + idx] = max(two[j * n + idx],one[j] + x)
            for x,idx in li[i]:
                one[idx] = max(one[idx],x)
        return ans
