class Solution {
public:
    typedef struct Node {
        Node(int y = -1, long long  value = -1000000000):y(y), value(value) {
            ;
        }
        int y;
        long long value;
    } TNode;
    long long maximumValueSum(vector<vector<int>>& board) {
        std::vector<std::vector<TNode> > V(board.size(), std::vector<Node>(3));
        for (int i = 0;i < board.size(); ++i) {
            for (int j = 0;j < board[i].size(); ++j) {
                if (board[i][j] >= V[i][0].value) {
                    V[i][2] = V[i][1];
                    V[i][1] = V[i][0];
                    V[i][0].value = board[i][j];
                    V[i][0].y = j;
                } else if (board[i][j] >= V[i][1].value) {
                    V[i][2] = V[i][1];
                    V[i][1].value = board[i][j];
                    V[i][1].y = j;
                } else if (board[i][j] > V[i][2].value) {
                    V[i][2].value = board[i][j];
                    V[i][2].y = j;
                }
            }
        }
        long long ans = -100000000000;
        for (int i = 0;i < board.size(); ++i) {
            for (int j = i+1;j < board.size(); ++j) {
                for (int k = j+1;k < board.size(); ++k) {
                    for (int x = 0;x < 3; ++x) {
                        for (int y = 0;y < 3; ++y) if (V[i][x].y != V[j][y].y) {
                            for (int z = 0;z < 3; ++z) if (V[i][x].y != V[k][z].y && V[j][y].y != V[k][z].y) {
                                long long tmp = V[i][x].value + V[j][y].value + V[k][z].value;
                                ans = std::max(tmp, ans);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
