class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        long long ans=-3e10;

        int m=b.size(), n=b[0].size();
        vector<tuple<int,int,int>> vec;
        for(int i=0; i<m; i++)
        {
            priority_queue<tuple<int,int,int>> pq;
            for(int j=0; j<n; j++)
            {
                pq.push(make_tuple(-b[i][j], i, j));
                if(pq.size()>3) pq.pop();
            }
            while(!pq.empty())
            {
                vec.push_back(pq.top());
                pq.pop();
            }
        }

        sort(vec.begin(), vec.end());

        int t=vec.size();

        for(int i=0; i<9; i++)
        {
            unordered_set<int> ur, uc;
            int val=-get<0>(vec[i]);
            int a=get<1>(vec[i]);
            int b=get<2>(vec[i]);
            ur.insert(a);
            uc.insert(b);

            long long curr=val;

            int valid=0;
            for(int j=i+1; j<t && valid<9; j++)
            {
                int val=-get<0>(vec[j]);
                int a=get<1>(vec[j]);
                int b=get<2>(vec[j]);

                if(ur.count(a) || uc.count(b)) continue;
                ++valid;
                ur.insert(a);
                uc.insert(b);
                curr+=val;

                for(int k=j+1; k<t; k++)
                {
                    int val=-get<0>(vec[k]);
                    int a=get<1>(vec[k]);
                    int b=get<2>(vec[k]);

                    if(ur.count(a) || uc.count(b)) continue;
                    curr+=val;
                    ans=max(ans, curr);
                    curr-=val;
                    break;
                }
                curr-=val;
                ur.erase(a);
                uc.erase(b);
            }
            ur.erase(a);
            uc.erase(b);
        }

        return ans;        
    }
};
