class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& bd) {
        long long ans=LONG_MIN;
        long long m=bd.size(),n=bd[0].size();
        vector<vector<pair<long long,long long>>>arr;
        for(long long i=0;i<bd.size();++i){
            vector<pair<long long,long long>>dum(3,{LONG_MIN,0});
            for(long long j=0;j<n;++j){
                if (bd[i][j] > dum[0].first) {
                    dum[2] = dum[1];
                    dum[1] = dum[0];
                    dum[0] = {bd[i][j], j};
                } else if (bd[i][j] > dum[1].first) {
                    dum[2] = dum[1];
                    dum[1] = {bd[i][j], j};
                } else if (bd[i][j] > dum[2].first) {
                    dum[2] = {bd[i][j], j};
                }
            }
            arr.push_back(dum);
        }
        // for(long long i=0;i<m;++i){
        //     for(long long j=0;j<3;++j){
        //         cout<<arr[i][0].first<<" "<<arr[i][1].second<<" --- ";
        //     }
        //     cout<<endl;
        // }
        for(long long i=0;i<m;++i){
            for(long long ii=0;ii<3;++ii){
                for(long long j=i+1;j<m;++j){
                    for(long long jj=0;jj<3;++jj){
                        for(long long k=j+1;k<m;++k){
                            for(long long kk=0;kk<3;++kk){
                                long long first=arr[i][ii].second;
                                long long n1=arr[i][ii].first;
                                long long sec=arr[j][jj].second;
                                long long n2=arr[j][jj].first;
                                long long n3=arr[k][kk].first;
                                long long third=arr[k][kk].second;
                                if(first!=sec && sec!=third && first!=third){
                                    ans=max(ans,n1+n2+n3);
                                }

                            }
                        }
                    }
                }
            }

        }
        return ans;
    }
};
