class Solution {
public:
    long long dp[101][102][102][3];
    long long solve(int row , int c1 , int c2 , int cnt , vector<vector<pair<int,int>>>&board){
           if(row==board.size()) {
               if(cnt==3) return 0;
               else return -1e16;
           }

           if(cnt == 3) return 0;

           if(dp[row][c1+1][c2+1][cnt]!=-1e18) return dp[row][c1+1][c2+1][cnt];

           long long ans = -1e16;

           ans = max(ans , 0 + solve(row+1,c1,c2,cnt,board));


           for(int i=0;i<3;i++){
                  if(board[row][i].second !=c1 && board[row][i].second!=c2){
                          if(cnt==0){
                                ans = max(ans , board[row][i].first + solve(row+1,board[row][i].second,c2,cnt+1,board));
                          }
                          else if(cnt == 1){
                                ans = max(ans , board[row][i].first + solve(row+1,c1,board[row][i].second,cnt+1,board));
                          }
                          else{
                                ans = max(ans , board[row][i].first + solve(row+1,c1,c2,cnt+1,board));
                          }
                  }
           }

        return dp[row][c1+1][c2+1][cnt] = ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
          vector<vector<pair<int,int>>>mat(board.size(),vector<pair<int,int>>(board[0].size()));

          for(int i=0;i<=100;i++){
                for(int j=0;j<=101;j++){
                     for(int k=0;k<=101;k++){
                           for(int p=0;p<=2;p++) dp[i][j][k][p] = -1e18;
                     }
                }
          }

          for(int i=0;i<board.size();i++){
                  for(int j=0;j<board[0].size();j++) mat[i][j] = make_pair(board[i][j],j);
          }

           for(int i=0;i<board.size();i++){
                 sort(mat[i].rbegin(),mat[i].rend());
          }

          return solve(0,-1,-1,0,mat);
    }
};
