class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = -1e17;

        int n = board.size();
        int m = board[0].size();


        vector<vector<long long>> g(n, vector<long long>(m));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                g[i][j] = board[i][j];
            }
        }

        vector<set<pair<int, int>>> rows(n);
        vector<set<pair<int, int>>> cols(m);


        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                rows[i].insert({g[i][j], j});
                cols[j].insert({g[i][j], i});

                while (rows[i].size() > 3) 
                    rows[i].erase(rows[i].begin());
                while (cols[j].size() > 3) 
                    cols[j].erase(cols[j].begin());
            }
        }

        vector<pair<int, int>> best;
        for (int i = 0; i < n; ++i) {
            for (auto [ele, col] : rows[i]) {
                if (cols[col].count({ele, i})) {
                    best.push_back({i, col});
                }
            }
        }

        int sz = best.size();
        for (int i = 0; i < sz; ++i)
            for (int j = i + 1; j < sz; ++j)
                    for (int k = j + 1; k < sz; ++k) {
                        if (best[i].first == best[j].first || best[i].first == best[k].first)
                            continue;
                        if (best[i].second == best[j].second || best[i].second == best[k].second)
                            continue;
                        if (best[j].second == best[k].second || best[j].first == best[k].first)
                            continue;

                        ans = max<long long>(ans, g[best[i].first][best[i].second] + g[best[j].first][best[j].second] + g[best[k].first][best[k].second]);
                    }

        return ans;
    }
};
