struct s{
    int i;
    int j;
    long long v;
    s(int i,int j,int v):i(i),j(j),v(v){}
    bool operator<(const s& n){return this->v>n.v;}
    bool operator>(const s& n){return this->v<n.v;}
};
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long max=-4e9,temp;
        int i,j,k;
        int m=board.size();
        int n=board[0].size();
        int l=m>n?m:n;
        vector<s> t;
        for (i=0;i<m;i++){
            for (j=0;j<n;j++)
                t.push_back(s(i,j,board[i][j]));
        }
        sort(t.begin(),t.end());
        for (int i=0;i<=3*l;i++){
            for (int j=i+1;j<=3*l+1;j++){
                if (t[i].i!=t[j].i&&t[i].j!=t[j].j)
                    for (k=j+1;k<=3*l+2;k++){
                        if (t[k].i!=t[i].i && t[k].i!=t[j].i && t[k].j!=t[i].j && t[k].j!=t[j].j){
                            temp=t[i].v+t[j].v+t[k].v;
                            if (temp>max) max=temp;
                        }
                    }
            }
        }
        return max;
    }
};
