#define x first
#define y second
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<pair<long long, int>>> v(m, vector(3, make_pair(LLONG_MIN, -1)));
        long long res = LLONG_MIN;
        for (int i = 0; i <  m; i++) {
            for (int j = 0; j < n; j++) {
                if (v[i][0].x <= board[i][j]) {
                    v[i][2] = v[i][1];
                    v[i][1] = v[i][0];
                    v[i][0] = {board[i][j], j};
                } else if (v[i][1].x <= board[i][j]) {
                    v[i][2] = v[i][1];
                    v[i][1] = {board[i][j], j};
                } else if (v[i][2].x < board[i][j]) {
                    v[i][2] = {board[i][j], j};
                }
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < m; j++) {
                if (i == j) {
                    continue;
                }
                for (int k = 0; k < m; k++) {
                    if (i == k || j == k) {
                        continue;
                    }
                    for (int ii = 0; ii < 3; ii++)
                    for (int jj = 0; jj < 3; jj++)
                    for (int kk = 0; kk < 3; kk++) {
                        if (v[i][ii].y != v[j][jj].y && v[i][ii].y != v[k][kk].y && v[j][jj].y != v[k][kk].y) {
                            res = max(res, v[i][ii].x + v[j][jj].x + v[k][kk].x);
                        }
                    }
                    // if (v[i][0].y != v[j][0] && v[i][0].y != v[k][0].y && v[j][0].y )
                }
            }
        }
        return res;
    }
};
