struct Point{
    long long val, l, r;
};

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();

        Point f[n][3];
        for(int i = 0; i < n; i++){
            f[i][0] = f[i][1] = f[i][2] = Point();
        }
        for(int i = 0; i < n; i++){
            vector<vector<long long>> v;
            for(int j = 0; j < m; j++){
                v.push_back({board[i][j], i, j});
            }

            sort(v.begin(), v.end());
            reverse(v.begin(), v.end());
            for(int j = 0; j < 3; j++){
                f[i][j] = {v[j][0], v[j][1], v[j][2]};
            }
        }

        long long qwq = -1000000000000000;

        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                for(int k = 0; k < n; k++){
                    for(int l = 0; l < 3; l++){
                        for(int p = 0; p < 3; p++){
                            for(int q = 0; q < 3; q++){
                                if(f[i][l].l == f[j][p].l || f[j][p].l == f[k][q].l || f[i][l].l == f[k][q].l) continue;
                                if(f[i][l].r == f[j][p].r || f[j][p].r == f[k][q].r || f[i][l].r == f[k][q].r) continue;
                                qwq = std::max(qwq, f[i][l].val + f[j][p].val + f[k][q].val);
                            }
                        }
                    }
                }
            }
        }
        return qwq;
    }
};
