#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

class Solution {
  public:
    ll maximumValueSum(vector<vector<int>> &v) {
        int n = v.sz;
        int m = v[0].sz;

        vector<vector<pii>> row(n);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                row[i].push_back({v[i][j], j});
                sort(row[i].begin(), row[i].end(), greater<>());

                if (row[i].sz == 4)
                    row[i].pop_back();
            }
        }

        ll ret = LLONG_MIN;

        for (int a = 0; a < n; a++) {
            for (int b = 0; b < a; b++) {
                for (int c = 0; c < b; c++) {
                    for (pii p : row[a]) {
                        for (pii q : row[b]) {
                            if (p.se == q.se)
                                continue;

                            for (pii r : row[c]) {
                                if (p.se == r.se || q.se == r.se)
                                    continue;

                                ret = max(ret, (ll)p.fi + q.fi + r.fi);
                            }
                        }
                    }
                }
            }
        }

        return ret;
    }
};
