class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        places = set()
        for i in range(len(board)):
            first = None
            second = None
            third = None
            for j in range(len(board[i])):
                if first is None or board[i][first] < board[i][j]:
                    third = second
                    second = first
                    first = j
                elif second is None or board[i][second] < board[i][j]:
                    third = second
                    second = j
                elif third is None or board[i][third] < board[i][j]:
                    third = j
            places.add((i, first))
            places.add((i, second))
            places.add((i, third))
        new_places = set()
        for j in range(len(board[0])):
            first = None
            second = None
            third = None
            for i in range(len(board)):
                if first is None or board[first][j] < board[i][j]:
                    third = second
                    second = first
                    first = i
                elif second is None or board[second][j] < board[i][j]:
                    third = second
                    second = i
                elif third is None or board[third][j] < board[i][j]:
                    third = i
            if (first, j) in places:
                new_places.add((first, j))
            if (second, j) in places:
                new_places.add((second, j))
            if (third, j) in places:
                new_places.add((third, j))

        array = sorted([(board[el[0]][el[1]], el[0], el[1]) for el in new_places])[::-1]
        n = 11
        if len(array) < n:
            n = len(array)

        value = None
        for i in range(n):
            for j in range(i + 1, n):
                for k in range(j + 1, n):
                    condition = value is None or value < array[i][0] + array[j][0] + array[k][0]
                    if array[i][1] == array[j][1]:
                        condition = False
                    if array[k][1] == array[j][1]:
                        condition = False
                    if array[i][1] == array[k][1]:
                        condition = False
                    if array[i][2] == array[j][2]:
                        condition = False
                    if array[k][2] == array[j][2]:
                        condition = False
                    if array[i][2] == array[k][2]:
                        condition = False
                    if condition:
                        value = array[i][0] + array[j][0] + array[k][0]

        return value
