class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board){
        int n=board.size();
        int m=board[0].size();
        vector<pair<int,pair<int,int>>>p;
        for(int i=0;i<n;++i){
            for(int j=0;j<m;++j){
                p.push_back({board[i][j],{i,j}});
            }
        }
        sort(p.begin(),p.end());
        reverse(p.begin(),p.end());
        int x1=-1;
        int y1=-1;
        int x2=-1;
        int y2=-1;
        long long ans=-1e15;
        for(int i=0;i<min(205,n*m);++i){
            long long cur=p[i].first;
            x1=p[i].second.first;
            y1=p[i].second.second;
            for(int j=i+1;j<min(i+1+405,n*m);++j){
                if(x1!=p[j].second.first and y1!=p[j].second.second){
                    x2=p[j].second.first;
                    y2=p[j].second.second;
                    cur+=p[j].first;
                    for(int k=j+1;k<n*m;++k){
                        if(x1!=p[k].second.first and y1!=p[k].second.second
                        and x2!=p[k].second.first and y2!=p[k].second.second){
                            cur+=p[k].first;
                            ans=max(ans,cur);
                            cur-=p[k].first;
                            break;
                        }
                    }
                    cur-=p[j].first;
                }
            }
        }
        return ans;
    }
};
