typedef long long ll ;
class Solution {
public:
    ll maxi(vector<vector<pair<ll , ll>>> check){
        ll ans = -1e10 ;
        for(int i = 0 ; i < 3 ; i++){
            for(int j = 0 ; j < 3 ; j++){
                for(int k = 0 ; k < 3 ; k++){
                    if(check[0][i].second == check[1][j].second) continue ;
                    if(check[1][j].second == check[2][k].second) continue ;
                    if(check[0][i].second == check[2][k].second) continue ;
                    ans = max(ans , check[0][i].first + check[1][j].first + check[2][k].first) ;
                }
            }
        }
        return ans ;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int row = board.size() , col = board[0].size() ;

        vector<vector<pair<ll , ll>>> updated(row , vector<pair<ll , ll>>(col , {0 , 0})) ;
        for(int i = 0 ; i < row ; i++){
            vector<pair<ll , ll>> tmp ;
            for(int j = 0 ; j < col ; j++){
                tmp.push_back({board[i][j] , j}) ;
            }
            updated[i] = tmp ;
        }
        for(int i = 0 ; i < row ; i++){
            sort(updated[i].begin() , updated[i].end()) ;
        }
        ll ans = -1e10 ;
        for(int i = 0 ; i < row ; i++){
            for(int j = i + 1 ; j < row ; j++){
                for(int k = j + 1 ; k < row ; k++){
                    vector<vector<pair<ll , ll>>> check(3 , vector<pair<ll , ll>>(3 , {0 , 0})) ;
                    check[0][0] = updated[i][col - 3] ;
                    check[0][1] = updated[i][col - 2] ;
                    check[0][2] = updated[i][col - 1] ;
                    check[1][0] = updated[j][col - 3] ;
                    check[1][1] = updated[j][col - 2] ;
                    check[1][2] = updated[j][col - 1] ;
                    check[2][0] = updated[k][col - 3] ;
                    check[2][1] = updated[k][col - 2] ;
                    check[2][2] = updated[k][col - 1] ;
                    ll tmp = maxi(check) ;
                    ans = max(ans , tmp) ;
                }
            }
        }
        return ans ; 
        // ll ans = 0 ;
    }
};
