using ll = long long;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int n = b.size(), m = b[0].size();
        vector<vector<pair<ll, ll>>> a(n, vector<pair<ll, ll>> (m));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                a[i][j] = {b[i][j], j};
            }
            sort(a[i].rbegin(), a[i].rend());
        }

        ll ans = INT64_MIN;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                for (int k = 0; k < n; ++k) {
                    if (i != k && j != k && i != j) {
                        int ii = 0;
                        ll cur = a[i][ii].first;
                        int jj = 0;
                        while (a[j][jj].second == a[i][ii].second) {
                            ++jj;
                        }
                        int kk = 0;
                        while (a[k][kk].second == a[i][ii].second || a[k][kk].second == a[j][jj].second) {
                            ++kk;
                        }
                        ans = max(ans, a[i][ii].first + a[j][jj].first + a[k][kk].first);
                    }
                }
            }
        }
        return ans;
    }
};
